import React, { useState } from 'react';
import Slider from './Slider';
import SidebarItem from './SidebarItem';
import './App.css';

const DEFAULT_OPTIONS = [
  {
    name: 'Brightness',
    property: 'brightness',
    value: 100,
    range: {min: 0, max: 200},
    unit: '%'
  },
  {
    name: 'Contrast',
    property: 'contrast',
    value: 100,
    range: {min: 0, max: 200},
    unit: '%'
  },
  {
    name: 'Saturation',
    property: 'saturate',
    value: 100,
    range: {min: 0, max: 200},
    unit: '%'
  }
]

function App() {
  const [selectedindex, setselectedindex] = useState(0);
  const [options, setoptions] = useState(DEFAULT_OPTIONS); 

  function GetImageStyle()
  {
    const filters = options.map((option) => {
      return `${option.property}(${option.value}${option.unit})`
    })

    return { filter: filters.join(' ') }
  }

  const handleDownload = () => {
    const image = document.querySelector('.image');
    const canvas = document.createElement('canvas');
    canvas.width = image.clientWidth;
    canvas.height = image.clientHeight;
    const ctx = canvas.getContext('2d');

    ctx.filter = GetImageStyle().filter;
    ctx.drawImage(image, 0, 0, canvas.width, canvas.height);


  };

  return (
    <div className="container">
      <div className="image" style={GetImageStyle()}>
        <button onClick={handleDownload}>Download</button>
      </div>
      <div className="sidebar">
        {options.map((options, index) => {
          return(<SidebarItem
            key={index}
            name={options.name}
            active = {selectedindex === index}
            handleClick={()=>{setselectedindex(index)}}
          />)
        })}
      </div>
      <Slider
        minVal={options[selectedindex].range.min}
        maxVal={options[selectedindex].range.max}
        Val={options[selectedindex].value}
        handleSlider={({target})=>{ 
          setoptions(prevOptions => {
            const newOptions = [...prevOptions]
            newOptions[selectedindex].value = target.value
            return newOptions
          });
        }}
      />
    </div>
  );
}

export default App;
