// Login.jsx

import React, { createContext, useState } from "react";
import "./Login.css"; // Import your CSS file
import { Link, Navigate } from "react-router-dom";
const url = 'https://photo-editor-d8if.onrender.com/login';

export default function Login() {
  const [isSignUp, setSignUp] = useState(false);
  const Loggedin = createContext(false);

  const handleSwitchToggle = () => {
    setSignUp(!isSignUp);
  };

  const handleLogin = () => {
    const username = document.getElementById('username').value;
    const password = document.getElementById('password').value;
    const email = document.getElementById('email').value;

    if (isSignUp) {
      // Call signup function
      handleSignUp(username, password, email);
    } else {
      // Call login function
      LoginCheck(username, password);
    }
  };

  async function LoginCheck(username, password) {
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username,
          password: password,
        }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log('Login successful:', data);
      // Do something with the data if needed

    } catch (error) {
      console.error('Error during login:', error.message);
      // Handle errors as needed
    }
  }

  async function handleSignUp(username, password, email) {
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username,
          password: password,
          email: email,
        }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log('Signup successful:', data);
      // Do something with the data if needed

    } catch (error) {
      console.error('Error during signup:', error.message);
      // Handle errors as needed
    }
  }

  return (
    <div className="login-container">
      <h3>Photo Editor</h3>
      <div className="switch-container">
        <label className="switch">
          <input type="checkbox" checked={isSignUp} onChange={handleSwitchToggle}/>
          <span className="slider round"></span>
        </label>
      </div>
      <div className="input-container">
        {isSignUp ? 
        <>
            <input id="username" placeholder="Username" className="login-input"/>
            <br />
            <input id="email" placeholder="Email" className="login-input"/>
            <br />
            <input id="password" type="password" placeholder="Password" className="login-input"/>
            <br />
            <input id="confirmPassword" type="password" placeholder="Confirm Password" className="login-input"/>
            <div className="password-helper">*Password must be at least 8 characters long*</div>
        </> :
        <>
            <input id="username" placeholder="Username" className="login-input"/>
            <br />
            <input id="password" type="password" placeholder="Password" className="login-input"/>
        </>}
        <br />
        <button className={isSignUp ? "signup-button" : "login-button"} onClick={handleLogin}>
          {isSignUp ? "Sign Up" : "Login"}
        </button>
        <br />
        <button className="google-login">Login with Google</button>
      </div>
    </div>
  );
}
